import './App.css';
import { useState } from 'react';

function App() {
  const [feedback, setFeedback] = useState('');

  // Simulate feedback for demonstration purposes
  const getFeedback = () => {
    const suggestions = [
      "Keep your back straight",
      "Lower your hips",
      "Maintain a consistent tempo",
      "Engage your core",
      "Don't lock your knees"
    ];
    const randomSuggestion = suggestions[Math.floor(Math.random() * suggestions.length)];
    setFeedback(randomSuggestion);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ProFormAI - Workout Form Analysis</h1>
        <p>Record your workout and get instant feedback!</p>

        {/* File input for video */}
        <input type="file" id="videoInput" accept="video/*" />

        {/* Button to simulate analysis */}
        <button onClick={getFeedback}>Analyze Form</button>

        {/* Display feedback */}
        <div className="feedback">
          {feedback && <p>Feedback: {feedback}</p>}
        </div>
      </header>
    </div>
  );
}

export default App;
